import { useState } from 'react';


export default function Main() {

  let [products, setProducts] = useState([
    {id: 1, name: 'Product 1', price:  100},
    {id: 2, name: 'Product 2', price:  110},
    {id: 3, name: 'Product 3', price:  150},
    {id: 4, name: 'Product 4', price:  160}
  ])

  function deleteProduct(id) {
    let f = products.filter(() => (singleProduct => singleProduct.id !== id))
  
    console.log(f)
    setProducts(f)
  }

  
  return ( 
    <div className='container my-5 d-flex gap-3'>
      {products.map(({name, price, id}) => (
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">{name}</h4>
              <p className="card-text">Price: {price}</p>
              <button className='btn btn-primary' onClick={() => deleteProduct({id})}>Delete</button>
            </div>
          </div>
          
      )) }
    </div>
  )
}
