import { useContext, useEffect, useState } from 'react';


export default function Main() {

  let cart = 0

  function plus() {
    cart++
    console.log(cart)
  }
  
  function minus() {
    cart--
    console.log(cart)
  }
  
  
  return ( 
    <>
    <span>{cart}</span>
    <br/>
    <button className='btn btn-primary me-4' onClick={plus}>+</button>
    
    <button className='btn btn-primary' onClick={minus}>-</button>
    </>
  )
}
