import React from 'react';
import ReactDOM from 'react-dom/client';
import Header from './Header'
import Main from './Main'

const root = ReactDOM.createRoot(document.getElementById('root'))

let product = [
  {name: 'product 1', description: 'some description here'},
  {name: 'product 2', description: 'some description here'},
  {name: 'product 3', description: 'some description here'}
];

function search(e) {
  let searchTerm = (e.target.value.toLowerCase()).trim()
  console.log(searchTerm)
  const pattern = '/' + searchTerm + '/';
  // let f = product.filter((singleProduct) => singleProduct.name.test(searchTerm) || singleProduct.description.test(searchTerm) )
  let f = product.filter((singleProduct) => pattern.test(singleProduct.name)  )

  console.log(f)
}


root.render(
  <>
  <Header fn={search} />
  <Main product={product}/>
  </>
)
